import Header from "../commons/Header/Header";
import Footer from "../commons/Footer/Footer";
import { Card } from "react-bootstrap";
import "./components.css";

function Films({ data }) {
  return (
    <>
      <Header />
      <h1 className="pplHeader">Films</h1>
      <div className="pplDiv">
        {data.map((films, i) => {
          return (
            <Card className="pplCard" style={{ width: "18rem" }} key={i}>
              <Card.Body>
                <Card.Title>{films.title}</Card.Title>
          <p>Director: {films.director}</p>
          <p>Producers: {films.producer}</p>
          <p>{films.release_date}</p>
              </Card.Body>
            </Card>
          );
        })}
      <Footer />
      </div>
    </>
  );
}

export default Films;
